import React, { Fragment, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { HomeAdminPageStyled } from './HomeAdminPage.style';
import { getRessources, deleteRessource } from '../../../services/apiServices'
import { Button } from '../../../components/ui-elements/Button/Button';

import { PostInfo } from '../../../components/PostInfo/PostInfo';
import { checkIfAdminByUserId } from '../../../utils/checkIfAdminByUserId';

const HomeAdminPage = () => {
  const [posts, setPosts] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const fetchMyAPI = async () => {
      const isAdmin = await checkIfAdminByUserId();
      if (!isAdmin) {
        history.push('/');
      }
      const response = await getRessources('post');
      setPosts(response);
    }

    fetchMyAPI();
  }, []);

  return (
    <HomeAdminPageStyled>
      <Button onClick={() => history.push('/admin/post/') } value="Posts: " />
      <Button onClick={() => history.push('/admin/categories/') } value="CatÃ©gories: " />
      <Button onClick={() => history.push('/admin/users/') } value="Utilisateurs: "/>
    </HomeAdminPageStyled>
  );
};

export { HomeAdminPage };
