import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router';
import { PostDetailsAdminStyled } from './PostDetailsAdmin.style';
import { getRessource } from '../../../services/apiServices';

const PostDetailsAdmin = () => {
  const [post, setPost] = useState(null);
  const { postId } = useParams();

  useEffect(() => {
    if (postId) {
      const fetchMyAPI = async () => {
        const data = await getRessource('post', postId);
        return data;
      }
      const data = fetchMyAPI();
      setPost(data);
    }
  }, [postId]);

  return (
    <PostDetailsAdminStyled>
      <div>
        <label>_id: </label>
        <input type="text" value={post._id} />
      </div>
      <div>
        <label>Title: </label>
        <input type="text" value={post.title} />
      </div>
      <div>
        <label>Description: </label>
        <input type="text" value={post.title_description} />
      </div>
      <div>
        <label>ImageUrl: </label>
        <input type="text" value={post.image} />
      </div>
      <div>
        <label>Contenu: </label>
        <input type="text" value={post.contenu} />
      </div>
    </PostDetailsAdminStyled>
  );
};

export { PostDetailsAdmin };
