import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { AdminPageStyled } from './AdminPage.style';
import { getRessources } from '../../services/apiServices'

import { PostInfo } from '../../components/PostInfo/PostInfo';

const AdminPage = () => {

  const [posts, setPosts] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      let response = await getRessources('post');
      setPosts(response);
    }
    fetchMyAPI();
  }, []);

  return (
    <AdminPageStyled>
      <h1>Liste des Posts</h1>
      {
        posts.map(post => (
          <div className="admin__posts-container">
            <PostInfo
              postId={post._id}
              title={post.title}
              description={post.title_description}
              imageUrl={post.image}
              content={post.contenu}
            />
            <div className="admin__posts-conatainer__button-container">
              <div>Supprimer</div>
              <div><Link to={`/admin/post/${post._id}`}>Modifier</Link></div>
            </div>
          </div>
        ))
      }
    </AdminPageStyled>
  );
};

export { AdminPage };
