import styled from 'styled-components';

export const PostDetailsAdminStyled = styled.div`
  margin: 20px;

  img {
    width: 100%;
    height: auto;
    margin-bottom: 10px;
  }

  .fied-container {
    display:flex;
    justify-content: space-between;

    label {
      width: 30%;
    }

    input {
      width: 70%;
    }
  }

  .button-container {
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 10px;

    button {
      margin: 0 10px;
      padding: 20px;
    }
  }
`;
