import styled from 'styled-components';

export const PostDetailsAdminStyled = styled.div`
  margin: 20px;
  width: 100%;

  img {
    with: 100%;
    height: auto,
  }

  .fied-container {
    display:flex;
    justify-content: space-between;

    label {
      width: 30%;
    }

    input {
      width: 70%;
    }
  }
`;
