import styled from 'styled-components';

export const PostDetailsStyled = styled.div`
  margin: 50px;

  img {
    display: flex;
    margin: auto;
    width: 100%;
    height: auto;
  }

  .postdetails__content {
    text-align: center;
  }
`;
