import React, { useState, useEffect, Fragment } from 'react';
import { useParams } from 'react-router';
import { useHistory } from "react-router-dom";
import { PostDetailsAdminStyled } from './PostDetailsAdmin.style';
import { getRessource , deleteRessource, updateRessource, createRessource } from '../../../services/apiServices';
import { PostForm } from './PostForm';

const PostDetailsAdmin = () => {
  const { postId } = useParams();
  const [inputs, setInputs] = useState({});
  const history = useHistory();

  const handleInputChange = (event) => {
    event.persist();
    setInputs(inputs => ({...inputs, [event.target.name]: event.target.value}));
  }

  const getValue = (name) => {
    return inputs[name] || "";
  }

  useEffect(() => {
    if (postId) {
      const fetchMyAPI = async () => {
        const data = await getRessource('post', postId);
        if (data) {
          setInputs({
            title: data.title,
            id: data._id,
            titleDesc: data.title_description,
            imageUrl: data.image,
            content: data.contenu,
          });
        }
      }
      fetchMyAPI();
    }
  }, [postId]);

  return (
    <PostDetailsAdminStyled>
      {postId ?
       getValue("id") ?
        <Fragment>
          <PostForm 
            handleInputChange={handleInputChange}
            getValue={getValue}
          />
          <div className="button-container">
            <button
              onClick={() => updateRessource('post', getValue("id"), {
                title: getValue("title"),
                title_description: getValue("titleDesc"),
                image: getValue("imageUrl"),
                _id: getValue("id"),
                contenu: getValue("content"),
              })}
              type="submit"
            >
              Mettre à jour
            </button>
            <button
              onClick={() => deleteRessource('post', getValue("id"))}
              type="submit"
            >
              Supprimer
            </button>
          </div>
        </Fragment>
        :
        <Fragment>
          Aucun résultat post avec l'id: {postId}
        </Fragment>
      :
        <Fragment>
          <PostForm 
            handleInputChange={handleInputChange}
            getValue={getValue}
          />
          <div className="button-container">
            <button
              onClick={() => {
                createRessource('post', {
                  title: getValue("title"),
                  title_description: getValue("titleDesc"),
                  image: getValue("imageUrl"),
                  contenu: getValue("content"),
                });
                history.push('/admin');
              }}
              type="submit"
            >
              Ajouter
            </button>
          </div>
        </Fragment>
      }
    </PostDetailsAdminStyled>
  );
};

export { PostDetailsAdmin };
