import React, {useEffect, useState} from 'react';
import { CatAdminPageStyled } from './CatAdminPage';
import { useParams } from 'react-router-dom';
import { getRessource, deleteRessource, updateRessource } from '../../../services/apiServices';
import { CatForm } from './CatForm';
import { Button } from '../../../components/ui-elements/Button/Button';

const CatEdit = () => {
  const params = useParams();
  const [inputs, setInputs] = useState({});
  const [categories, setCategories] = useState([]);

  const catId = params.catId;

  const handleInputChange = (event) => {
    event.persist();
    setInputs(inputs => ({...inputs, [event.target.name]: event.target.value}));
  }

  const getValue = (name) => {
    return inputs[name] || "";
  }

  useEffect(() => {
    if (!catId) return null;

    getRessource('cat', catId).then(data => {
      if (data) {
        setInputs({
          catName: data.catName,
          id: data._id,
          imageUrl: data.image,
        });
      }
    });
  }, [catId]);

  return (
    <CatAdminPageStyled>
      <CatForm 
        handleInputChange={handleInputChange}
        getValue={getValue}
        categories={categories}
      />
      <div className="button-container">
        <Button
          onClick={() => updateRessource('post', getValue("id"), {
            title: getValue("title"),
            title_description: getValue("titleDesc"),
            image: getValue("imageUrl"),
            _id: getValue("id"),
            contenu: getValue("content"),
            category: getValue("category"), 
          })}
          type="submit"
          value="Mettre Ã  jour"
        />
        <Button
          onClick={() => deleteRessource('post', getValue("id"))}
          type="submit"
          value="supprimer"
        />
      </div>
    </CatAdminPageStyled>
  );
}

export { CatEdit }
