import styled from 'styled-components';

export const PostInfoStyled = styled.div`
  a {
    text-decoration: none;
    color: black;
    cursor: pointer;
    display: flex;
    margin: 20px;

    img {
      width: 300px;
      height: auto; 
    }

    .postinfo__content {
      margin: 10px;
    }
  }
`;
