import styled from 'styled-components';

export const HeaderStyled = styled.div`
  nav {
    width: 100%;
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    background-color: grey;
    margin: 0;

    div {
      width: 100px;
      padding: 30px;
    }

    ul {
      width: 100%;
      display: flex;
      justify-content: space-evenly;
      margin: 0;
      padding: 0;

      li {
        cursor: pointer;
        padding: 30px;
        list-style: none;
        width: auto;
        display: flex;
        align-items: center;
      }
    }
  }
`;
