import React, { Fragment, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { UserAdminPageStyled } from './UserAdminPage.style';
import { getRessources, deleteRessource } from '../../../services/apiServices'
import { Button } from '../../../components/ui-elements/Button/Button';

const UserAdminPage = () => {
  const [users, setUsers] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const fetchMyAPI = async () => {
      const response = await getRessources('user', localStorage.getItem('jwtToken'));
      setUsers(response);
    }

    fetchMyAPI();
  }, []);

  const removePost = (id) => {
    deleteRessource('user', id).then(data => {
      if (data.success) {
        const newPost = users.filter(user => user._id !== id);
        setUsers(newPost);
      }
    })
  }

  console.log('users :>> ', users);

  return (
    <UserAdminPageStyled>
      <h1>Liste des users: </h1>
      { users && users.length > 0 ?
          users.map(user => (
            <div key={user._id}>
              {user.pseudo}
            </div>
          ))    
      :
        <Fragment>
          Aucun users trouv√©.
        </Fragment>
      }
    </UserAdminPageStyled>
  );
};

export { UserAdminPage };
