import React, { useState, useEffect, Fragment } from 'react';
import { useParams } from 'react-router';
import { PostDetailsAdminStyled } from './PostDetailsAdmin.style';
import { getRessource , deleteRessource, updateRessource} from '../../../services/apiServices';

const PostDetailsAdmin = () => {
  const [post, setPost] = useState(null);
  const { postId } = useParams();

  const [title, setTitle] = useState("");
  const [titleDesc, setTitleDesc] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [content, setContent] = useState("");

  useEffect(() => {
    if (postId) {
      const fetchMyAPI = async () => {
        const data = await getRessource('post', postId);
        setPost(data);
      }
      fetchMyAPI();
    }
  }, [postId]);
  console.log('post :>> ', post);
  return (
    <PostDetailsAdminStyled>
      {post && 
        <Fragment>
          <img src={post.image} alt={post.title} />
          <div className="fied-container">
            <label>_id: </label>
            <input type="text" value={post._id} />
          </div>
          <div className="fied-container">
            <label>Title: </label>
            <input type="text" value={post.title} />
          </div>
          <div className="fied-container">
            <label>Description: </label>
            <input type="text" value={post.title_description} />
          </div>
          <div className="fied-container">
            <label>ImageUrl: </label>
            <input type="text" value={post.image} />
          </div>
          <div className="fied-container">
            <label>Contenu: </label>
            <input type="text" value={post.contenu} />
          </div>
          <div className="button-container">
            <button
              onClick={() => updateRessource('post', post._id), JSON.stringify({
                title
              })}
              type="submit"
            >
              Mettre à jour
            </button>
            <button
              onClick={() => deleteRessource('post', post._id)}
              type="submit"
            >
              Supprimer
            </button>
          </div>
        </Fragment>
      }
    </PostDetailsAdminStyled>
  );
};

export { PostDetailsAdmin };
