import React, { useState, useEffect, Fragment } from 'react';
import { useParams } from 'react-router';
import { PostDetailsAdminStyled } from './PostDetailsAdmin.style';
import { getRessource , deleteRessource, updateRessource} from '../../../services/apiServices';
import { PostForm } from './PostForm';

const PostDetailsAdmin = () => {
  const { postId } = useParams();
  const [inputs, setInputs] = useState({});

  const handleInputChange = (event) => {
    event.persist();
    setInputs(inputs => ({...inputs, [event.target.name]: event.target.value}));
  }

  const getValue = (name) => {
    return inputs[name] || "";
  }
  console.log('inputs :>> ', inputs);

  useEffect(() => {
    if (postId) {
      const fetchMyAPI = async () => {
        const data = await getRessource('post', postId);
        console.log('data :>> ', data);
        if (data && data.length > 0) {
          setTitle(data.title);
          setId(data._id);
          setTitleDesc(data.title_description);
          setImageUrl(data.image);
          setContent(data.contenu);
        }
      }
      fetchMyAPI();
    }
  }, [postId]);

  return (
    <PostDetailsAdminStyled>
      {postId && getValue("id") &&
        <Fragment>
          <PostForm 
            handleInputChange={handleInputChange}
            getValue={getValue}
          />
          <div className="button-container">
            <button
              onClick={() => updateRessource('post', getValue("id"), {
                title: getValue("title"),
                title_description: getValue("titleDesc"),
                image: getValue("imageUrl"),
                _id: getValue("id"),
                contenu: getValue("content"),
              })}
              type="submit"
            >
              Mettre Ã  jour
            </button>
            <button
              onClick={() => deleteRessource('post', getValue("id"))}
              type="submit"
            >
              Supprimer
            </button>
          </div>
        </Fragment>
      }
      {!postId &&
        <Fragment>
        <PostForm 
          handleInputChange={handleInputChange}
          getValue={getValue}
        />
        <div className="button-container">
          <button
            onClick={() => deleteRessource('post', getValue("id"))}
            type="submit"
          >
            Supprimer
          </button>
        </div>
      </Fragment>
      }
    </PostDetailsAdminStyled>
  );
};

export { PostDetailsAdmin };
