import React, { Fragment, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { AdminPageStyled } from './AdminPage.style';
import { getRessources, deleteRessource } from '../../services/apiServices'

import { PostInfo } from '../../components/PostInfo/PostInfo';

const AdminPage = () => {

  const [posts, setPosts] = useState([]);
  const history = useHistory();

  useEffect(() => {
    async function fetchMyAPI() {
      let response = await getRessources('post');
      setPosts(response);
    }
    fetchMyAPI();
  }, []);

  return (
    <AdminPageStyled>
      <h1>Liste des Posts: </h1>
      <div onClick={() => history.push('/admin/post')}>
        Ajouter un post
      </div>
      { posts && posts.length > 0 ?
          posts.map(post => (
            <div className="admin__posts-container">
              <PostInfo
                postId={post._id}
                title={post.title}
                description={post.title_description}
                imageUrl={post.image}
                content={post.contenu}
              />
              <div className="admin__posts-container__button-container">
                <button type="submit" onClick={() => deleteRessource('post', post._id)}>
                  Supprimer
                </button>
                <button onClick={() => history.push(`/admin/post/${post._id}`)}>Modifier</button>
              </div>
            </div>
          ))
        :
        <Fragment>
          Aucun post pour le moment
        </Fragment>
      }
    </AdminPageStyled>
  );
};

export { AdminPage };
