import axios from 'axios';
const BASE_URL = process.env.API_URL;

export const getRessources = async (name) => {
  const url = `${BASE_URL}/${name}`;
  const response = await axios.get(url);
  return response.data;
}

export const getRessource = (name, id) => {
  const url = `${BASE_URL}/${name}/${id}`;
  return axios.get(url).then((response) => response.data);
}

export const updateRessource = (name, id, body) => {
  const url = `${BASE_URL}/${name}/${id}`;
  return axios.put(url, body).then((response) => response.data);
}

export const createRessource = (name, body) => {
  const url = `${BASE_URL}/${name}`;
  axios.post(url, body).then((response) => response.data);
}

export const deleteRessource = (name, id) => {
  const url = `${BASE_URL}/${name}/${id}`;
  return axios.delete(url).then((response) => response.data);
}
