import React, { useState, useEffect, Fragment } from 'react';
import { useParams } from 'react-router';
import { PostDetailsAdminStyled } from './PostDetailsAdmin.style';
import { getRessource , deleteRessource, updateRessource} from '../../../services/apiServices';

const PostDetailsAdmin = () => {
  const { postId } = useParams();
  const [id, setId] = useState("");
  const [title, setTitle] = useState("");
  const [titleDesc, setTitleDesc] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [content, setContent] = useState("");

  useEffect(() => {
    if (postId) {
      const fetchMyAPI = async () => {
        const data = await getRessource('post', postId);
        console.log('data :>> ', data);
        if (data && data.length > 0) {
          setTitle(data.title);
          setId(data._id);
          setTitleDesc(data.title_description);
          setImageUrl(data.image);
          setContent(data.contenu);
        }
      }
      fetchMyAPI();
    }
  }, [postId]);

  return (
    <PostDetailsAdminStyled>
      {id && id.length > 0 ?
        <Fragment>
          <img src={imageUrl} alt={title} />
          <div className="fied-container">
            <label>_id: </label>
            <input onChange={(e) => setId(e.target.value)} type="text" value={id} />
          </div>
          <div className="fied-container">
            <label>Title: </label>
            <input onChange={(e) => setTitle(e.target.value)} type="text" value={title} />
          </div>
          <div className="fied-container">
            <label>Description: </label>
            <input onChange={(e) => setTitleDesc(e.target.value)} type="text" value={titleDesc} />
          </div>
          <div className="fied-container">
            <label>ImageUrl: </label>
            <input onChange={(e) => setImageUrl(e.target.value)} type="text" value={imageUrl} />
          </div>
          <div className="fied-container">
            <label>Contenu: </label>
            <textarea onChange={(e) => setContent(e.target.value)} type="text" value={content} />
          </div>
          <div className="button-container">
            <button
              onClick={() => updateRessource('post', id, {
                title,
                title_description: titleDesc,
                image: imageUrl,
                _id: id,
                contenu: content,
              })}
              type="submit"
            >
              Mettre à jour
            </button>
            <button
              onClick={() => deleteRessource('post', id)}
              type="submit"
            >
              Supprimer
            </button>
          </div>
        </Fragment>
        :
        <Fragment>
          Aucun post trouvé avec cette id: {postId}
        </Fragment>
      }
    </PostDetailsAdminStyled>
  );
};

export { PostDetailsAdmin };
