import React, { Fragment, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { AdminPageStyled } from './AdminPage.style';
import { getRessources, deleteRessource } from '../../services/apiServices'

import { PostInfo } from '../../components/PostInfo/PostInfo';

const AdminPage = () => {

  const [posts, setPosts] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      let response = await getRessources('post');
      setPosts(response);
    }
    fetchMyAPI();
  }, []);

  return (
    <AdminPageStyled>
      <h1>Liste des Posts: </h1>
      <div>
        <Link to="/admin/post">Ajouter un post</Link>
      </div>
      { posts && posts.length > 0 ?
          posts.map(post => (
            <div className="admin__posts-container">
              <PostInfo
                postId={post._id}
                title={post.title}
                description={post.title_description}
                imageUrl={post.image}
                content={post.contenu}
              />
              <div className="admin__posts-conatainer__button-container">
                <div onClick={() => deleteRessource('post', post._id)}>Supprimer</div>
                <div><Link to={`/admin/post/${post._id}`}>Modifier</Link></div>
              </div>
            </div>
          ))
        :
        <Fragment>
          Aucun post pour le moment
        </Fragment>
      }
    </AdminPageStyled>
  );
};

export { AdminPage };
